# Bernoulli Naive Bayes Classifier Implementation
class BernoulliNaiveBayes:
    def fit(self, X, y):
        #finds the unique classes in the target labels y
        self.classes = np.unique(y)
        #This dictionary will store the prior probabilities for each class. P(c)
        self.class_probs = {}
        self.feature_probs = {}
        #This step calculates the probability of each feature (pixel) being 1 for class c.
        #It uses Laplace smoothing (add-one smoothing) by adding 1 to the numerator and 2 to the denominator.
        #This helps prevent zero probabilities when a feature is absent from a class and helps with robustness.
        for c in self.classes:
            class_indices = (y == c)
            self.class_probs[c] = sum(class_indices) / len(y)
            self.feature_probs[c] = (X[class_indices].sum(axis=0) + 1) / (sum(class_indices) + 2)

    def predict(self, X):
        predictions = []
        for sample in X:
            #This dictionary will store the log-likelihood scores for each class for the current test sample. P(c/x)
            class_scores = {}
            for c in self.classes:
                class_probs = np.log(self.class_probs[c])
                feature_probs = np.log(self.feature_probs[c]) * sample + np.log(1 - self.feature_probs[c]) * (1 - sample)
                class_scores[c] = class_probs + feature_probs.sum()
            predictions.append(max(class_scores, key=class_scores.get))
        return predictions

